import axios from "axios";

const tasksURL = "http://localhost:8080/tasks/";


class TaskService {

  async getTasks() {
    const username = localStorage.getItem('username');
    const result = await axios.get(tasksURL + username);
    return result.data;
  }

  removeTask() {
  }

}

export default new TaskService;
