import React from "react";
import TaskService from "../service/TaskService";

function TaskListForm() {

  const taskList = TaskService.getTasks();

  return (
    <div className="task-list">
      <table>
        {taskList.map(task =>
          <tr key={task.id}>
            <td>{task.taskBody}</td>
          </tr>
        )}
      </table>

    </div>
  );
}


export default TaskListForm;
